mapper.update:
update tbl_ask_board set
	a_title = ${a_title},
	a_content = ${a_content}
where a_bno = #{a_bno}

mapper.select:
select * from(select rownum rnum, a.* from(
	select * from tbl_ask_board
	where writer = #{writer}
	or re_group = #{re_group}
	order by re_group desc, re_seq asc) a)
where rnum between #{startRow} and #{endRow}

mapper.update:
update tbl_ask_board set
	re_seq = re_seq + 1
where re_group = #{re_group}
and re_seq > #{re_seq}

update tbl_ask_board set
	a_state = a_state + 1
where a_bno = #{a_bno}
