mapper.update:
update tbl_inquiry_board set
	i_title = ${i_title},
	i_content = ${i_content}
where i_bno = #{i_bno}

mapper.select:
select * from(select rownum rnum, a.* from(
	select * from tbl_inquiry_board) a)
where rnum between #{startRow} and #{endRow}

mapper.update:
update tbl_inquiry_board set
	re_seq = re_seq + 1
where re_group = #{re_group}
and re_seq > #{re_seq}
