mapper.select:
select * from tbl_member
where userid = #{userid}
and userpw = #{userpw}

mapper.update:
update tbl_member set
	m_picture = #{m_picture, jdbcType=VARCHAR}
	where userid = #{userid}

update tbl_member set
	userpw = #{userpw}
where userid = #{userid} and username = #{username} and email = #{email}

update tbl_member set
	m_totalattend = 0
where userid = #{userid}
